		//Random array
		function random_array(raw_array ) {
		    var i, j, tmp

		    for ( i = raw_array.length - 1; i > 0; i-- ) { // swap elements in array randomly using Fisher-Yates (aka Knuth) Shuffle
		        j = Math.floor( Math.random() * (i + 1) );
		        tmp = raw_array[i];
		        raw_array[i] = raw_array[j];
		        raw_array[j] = tmp;
		    }
		    return raw_array;
		}
		
		
