        MapMgr.prototype.getLatLngToPix = function(pos) {
        	var p = null;
        	var overlay = this._overlayForLatLng;
        	if (overlay == null) {
        		return p;
        	}
        	var proj = overlay.getProjection();
        	if (proj != null) {
        		p = proj.fromLatLngToContainerPixel(pos);
        	}
        	return p;
        }
        
